
public class Ex15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Math.abs() = retorna el valor absolut (positiu si es negatiu)
		// Math.round() = redondea hacia arriba, 5.5 = 6 | hacia abajo 5.4 = 4
		// Math.pow() = dona la potencia de un valor, per aquesta

		// a:
		System.out.println(Math.pow(3, 7));

		// b:
		System.out.println(Math.floor(34.7));

		// c:
		System.out.println(Math.round(34.7));

		// d:
		int valorE = -86;

		double valorD = (double) valorE;

		Math.abs(valorD);

		System.out.println(valorD);

	}

}
